import React, { useState, useEffect } from "react";
import { Progress } from "@/components/ui/progress";

const GoalTracker = () => {
  const [goals, setGoals] = useState([]);
  const [goalText, setGoalText] = useState("");
  const [progress, setProgress] = useState(0);
  const [target, setTarget] = useState(1);
  const [level, setLevel] = useState("easy");
  const [reward, setReward] = useState("");
  const [filter, setFilter] = useState("all");
  const [editingGoalId, setEditingGoalId] = useState(null);
  const [baseIncrement, setBaseIncrement] = useState(1);
  const [priority, setPriority] = useState("medium");

  useEffect(() => {
    const storedGoals = JSON.parse(localStorage.getItem("goals")) || [];
    setGoals(storedGoals);
  }, []);

  useEffect(() => {
    localStorage.setItem("goals", JSON.stringify(goals));
  }, [goals]);

  const addGoal = () => {
    if (!goalText.trim()) return;

    if (editingGoalId !== null) {
      setGoals(
        goals.map((g) =>
          g.id === editingGoalId
            ? {
                ...g,
                text: goalText,
                target: parseInt(target),
                level,
                reward,
                increment: baseIncrement,
                priority,
              }
            : g
        )
      );
      setEditingGoalId(null);
    } else {
      setGoals([
        ...goals,
        {
          id: Date.now(),
          text: goalText,
          progress: 0,
          target: parseInt(target),
          level,
          reward,
          increment: baseIncrement,
          priority,
        },
      ]);
    }

    setGoalText("");
    setTarget(1);
    setLevel("easy");
    setReward("");
    setBaseIncrement(1);
    setPriority("medium");
  };

  const updateProgress = (id, amount) => {
    setGoals(
      goals.map((g) =>
        g.id === id
          ? { ...g, progress: Math.min(g.progress + amount, g.target) }
          : g
      )
    );
  };

  const deleteGoal = (id) => {
    setGoals(goals.filter((g) => g.id !== id));
  };

  const startEditing = (goal) => {
    setEditingGoalId(goal.id);
    setGoalText(goal.text);
    setTarget(goal.target);
    setLevel(goal.level);
    setReward(goal.reward);
    setBaseIncrement(goal.increment);
    setPriority(goal.priority || "medium");
  };

  const priorityMap = { high: 3, medium: 2, low: 1 };
  goals.sort(
    (a, b) =>
      priorityMap[b.priority || "medium"] - priorityMap[a.priority || "medium"]
  );

  const filteredGoals = goals.filter((goal) => {
    if (filter === "all") return true;
    if (filter === "completed") return goal.progress >= goal.target;
    if (filter === "active") return goal.progress < goal.target;
    return true;
  });

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Goal Tracker</h1>
      <div className="grid grid-cols-1 gap-4 mb-4">
        <input
          className="border rounded p-2"
          placeholder="Enter your goal"
          value={goalText}
          onChange={(e) => setGoalText(e.target.value)}
        />
        <input
          className="border rounded p-2"
          type="number"
          min="1"
          placeholder="Target"
          value={target}
          onChange={(e) => setTarget(e.target.value)}
        />
        <select
          className="border rounded p-2"
          value={level}
          onChange={(e) => setLevel(e.target.value)}
        >
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
        <input
          className="border rounded p-2"
          placeholder="Reward"
          value={reward}
          onChange={(e) => setReward(e.target.value)}
        />
        <input
          className="border rounded p-2"
          type="number"
          min="1"
          placeholder="Progress Increment"
          value={baseIncrement}
          onChange={(e) => setBaseIncrement(parseInt(e.target.value))}
        />
        <select
          className="border rounded p-2"
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
        >
          <option value="low">Low Priority</option>
          <option value="medium">Medium Priority</option>
          <option value="high">High Priority</option>
        </select>
        <button
          className="bg-blue-500 text-white p-2 rounded"
          onClick={addGoal}
        >
          {editingGoalId !== null ? "Update Goal" : "Add Goal"}
        </button>
      </div>

      <div className="mb-4">
        <button className="mr-2" onClick={() => setFilter("all")}>
          All
        </button>
        <button className="mr-2" onClick={() => setFilter("active")}>
          Active
        </button>
        <button onClick={() => setFilter("completed")}>Completed</button>
      </div>

      {filteredGoals.map((g) => (
        <div key={g.id} className="border rounded p-4 mb-2 shadow">
          <h2 className="text-lg font-semibold">{g.text}</h2>
          <p className="text-xs text-gray-500">
            Level: {g.level} | Reward: {g.reward} | Priority: {g.priority}
          </p>
          <Progress value={(g.progress / g.target) * 100} className="my-2" />
          <div className="flex items-center gap-2">
            <button
              className="bg-green-500 text-white px-2 py-1 rounded"
              onClick={() => updateProgress(g.id, g.increment)}
            >
              +{g.increment}
            </button>
            <button
              className="bg-yellow-500 text-white px-2 py-1 rounded"
              onClick={() => startEditing(g)}
            >
              Edit
            </button>
            <button
              className="bg-red-500 text-white px-2 py-1 rounded"
              onClick={() => deleteGoal(g.id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default GoalTracker;
