import React, { useState, useEffect } from "react";
import confetti from "canvas-confetti";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const App = () => {
  const [goals, setGoals] = useState(() => {
    const stored = localStorage.getItem("goals");
    return stored ? JSON.parse(stored) : [];
  });
  const [goalText, setGoalText] = useState("");
  const [target, setTarget] = useState(1);
  const [level, setLevel] = useState("beginner");
  const [reward, setReward] = useState("immediate");
  const [filter, setFilter] = useState("all");
  const [sortOrder, setSortOrder] = useState("default");
  const [editingGoalId, setEditingGoalId] = useState(null);

  useEffect(() => {
    localStorage.setItem("goals", JSON.stringify(goals));
  }, [goals]);
  const addGoal = () => {
    if (!goalText || target <= 0) return;
    const baseIncrement = level === "expert" ? 2 : 1;

    if (editingGoalId !== null) {
      setGoals(
        goals.map((g) =>
          g.id === editingGoalId
            ? {
                ...g,
                text: goalText,
                target: parseInt(target),
                level,
                reward,
                increment: baseIncrement,
                stepCount: 0, // reset
              }
            : g
        )
      );
      setEditingGoalId(null);
    } else {
      setGoals([
        ...goals,
        {
          id: Date.now(),
          text: goalText,
          progress: 0,
          target: parseInt(target),
          level,
          reward,
          increment: baseIncrement,
          stepCount: 0, // new field
        },
      ]);
    }

    setGoalText("");
    setTarget(1);
    setLevel("beginner");
    setReward("immediate");
  };
  const updateProgress = (id, direction) => {
    setGoals((prevGoals) =>
      prevGoals.map((goal) => {
        if (goal.id !== id) return goal;

        const stepChange = direction === "increase" ? 1 : -1;
        const newStepCount = Math.max(0, goal.stepCount + stepChange);

        const stepValue = 2 * newStepCount + 1; // e.g. stepCount 0 → +1, 1 → +3, 2 → +5, etc.
        const newProgress =
          direction === "increase"
            ? Math.min(goal.progress + stepValue, goal.target)
            : Math.max(goal.progress - stepValue, 0);

        if (newProgress === goal.target && direction === "increase") {
          confetti({
            particleCount: 100,
            spread: 70,
            origin: { y: 0.6 },
          });
        }

        return {
          ...goal,
          progress: newProgress,
          stepCount: newStepCount,
        };
      })
    );
  };
  const deleteGoal = (id) => {
    setGoals((prevGoals) => prevGoals.filter((goal) => goal.id !== id));
  };

  const editGoal = (id, newText, newTarget) => {
    setGoals((prevGoals) =>
      prevGoals.map((goal) =>
        goal.id === id
          ? {
              ...goal,
              text: newText,
              target: Number(newTarget),
              progress: Math.min(goal.progress, Number(newTarget)), // ensure progress doesn’t exceed target
            }
          : goal
      )
    );
  };
  {
    goals.map((goal) => (
      <div
        key={goal.id}
        className="border p-4 rounded-lg shadow-md mb-4 bg-white"
      >
        <h2 className="text-lg font-semibold mb-2">{goal.text}</h2>
        <div className="w-full bg-gray-200 rounded-full h-4 mb-2">
          <div
            className="bg-blue-500 h-4 rounded-full"
            style={{ width: `${(goal.progress / goal.target) * 100}%` }}
          />
        </div>
        <p className="text-sm mb-2">
          {goal.progress} / {goal.target}
        </p>
        <div className="flex gap-2">
          <button
            className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
            onClick={() => updateProgress(goal.id, "increase")}
          >
            +
          </button>
          <button
            className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
            onClick={() => updateProgress(goal.id, "decrease")}
          >
            -
          </button>
          <button
            className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
            onClick={() => deleteGoal(goal.id)}
          >
            Delete
          </button>
          {/* You can replace this with a modal or inline edit */}
          {/* <button onClick={() => editGoal(goal.id, 'New Text', 200)}>Edit</button> */}
        </div>
      </div>
    ));
  }
};

export default App;
