import React, { useState, useEffect } from "react";

export default function GoalTracker() {
  const [goals, setGoals] = useState([]);
  const [goalText, setGoalText] = useState("");
  const [target, setTarget] = useState("");
  const [level, setLevel] = useState("");
  const [reward, setReward] = useState("");
  const [baseIncrement, setBaseIncrement] = useState(1);
  const [filter, setFilter] = useState("all");
  const [editingGoalId, setEditingGoalId] = useState(null);
  const [priority, setPriority] = useState("medium");

  useEffect(() => {
    const storedGoals = JSON.parse(localStorage.getItem("goals"));
    if (storedGoals) setGoals(storedGoals);
  }, []);

  useEffect(() => {
    localStorage.setItem("goals", JSON.stringify(goals));
  }, [goals]);

  const addGoal = () => {
    if (!goalText || !target || !level || !reward) return;
    if (editingGoalId !== null) {
      setGoals(
        goals.map((g) =>
          g.id === editingGoalId
            ? {
                ...g,
                text: goalText,
                target: parseInt(target),
                level,
                reward,
                increment: baseIncrement,
                priority,
              }
            : g
        )
      );
      setEditingGoalId(null);
    } else {
      setGoals([
        ...goals,
        {
          id: Date.now(),
          text: goalText,
          progress: 0,
          target: parseInt(target),
          level,
          reward,
          increment: baseIncrement,
          priority,
        },
      ]);
    }
    setGoalText("");
    setTarget("");
    setLevel("");
    setReward("");
    setBaseIncrement(1);
    setPriority("medium");
  };

  const incrementProgress = (id, inc) => {
    setGoals(
      goals.map((g) =>
        g.id === id
          ? { ...g, progress: Math.min(g.progress + inc, g.target) }
          : g
      )
    );
  };

  const deleteGoal = (id) => setGoals(goals.filter((g) => g.id !== id));

  const startEditing = (goal) => {
    setGoalText(goal.text);
    setTarget(goal.target);
    setLevel(goal.level);
    setReward(goal.reward);
    setBaseIncrement(goal.increment);
    setPriority(goal.priority || "medium");
    setEditingGoalId(goal.id);
  };

  const priorityMap = { high: 3, medium: 2, low: 1 };
  goals.sort(
    (a, b) =>
      priorityMap[b.priority || "medium"] - priorityMap[a.priority || "medium"]
  );

  const filteredGoals = goals.filter((g) => {
    if (filter === "completed") return g.progress >= g.target;
    if (filter === "inProgress") return g.progress < g.target;
    return true;
  });

  return (
    <div className="max-w-2xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Goal Tracker</h1>

      <div className="space-y-2 mb-4">
        <input
          className="border p-2 w-full"
          placeholder="Goal"
          value={goalText}
          onChange={(e) => setGoalText(e.target.value)}
        />
        <input
          className="border p-2 w-full"
          placeholder="Target"
          type="number"
          value={target}
          onChange={(e) => setTarget(e.target.value)}
        />
        <input
          className="border p-2 w-full"
          placeholder="Level"
          value={level}
          onChange={(e) => setLevel(e.target.value)}
        />
        <input
          className="border p-2 w-full"
          placeholder="Reward"
          value={reward}
          onChange={(e) => setReward(e.target.value)}
        />
        <input
          className="border p-2 w-full"
          placeholder="Increment (default 1)"
          type="number"
          value={baseIncrement}
          onChange={(e) => setBaseIncrement(parseInt(e.target.value))}
        />
        <select
          className="border rounded p-2 w-full"
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
        >
          <option value="low">Low Priority</option>
          <option value="medium">Medium Priority</option>
          <option value="high">High Priority</option>
        </select>
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={addGoal}
        >
          {editingGoalId !== null ? "Update Goal" : "Add Goal"}
        </button>
      </div>

      <div className="mb-4">
        <button onClick={() => setFilter("all")} className="mr-2">
          All
        </button>
        <button onClick={() => setFilter("inProgress")} className="mr-2">
          In Progress
        </button>
        <button onClick={() => setFilter("completed")}>Completed</button>
      </div>

      {filteredGoals.map((g) => (
        <div key={g.id} className="border rounded p-4 mb-2">
          <h2 className="text-lg font-semibold">{g.text}</h2>
          <p className="text-xs text-gray-500">
            Level: {g.level} | Reward: {g.reward} | Priority: {g.priority}
          </p>
          <p>
            Progress: {g.progress}/{g.target}
          </p>
          <div className="space-x-2 mt-2">
            <button
              className="bg-green-500 text-white px-2 py-1 rounded"
              onClick={() => incrementProgress(g.id, g.increment)}
            >
              +{g.increment}
            </button>
            <button
              className="bg-yellow-500 text-white px-2 py-1 rounded"
              onClick={() => startEditing(g)}
            >
              Edit
            </button>
            <button
              className="bg-red-500 text-white px-2 py-1 rounded"
              onClick={() => deleteGoal(g.id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}
